<span class="close-popup" onclick="closePopup();"><span class="icon-close"></span></span><h3 class="title-popup">Book a call today</h3><form id="callback_form" class="popup-form callback_form">    <div class="pf-flex">        <div class="pf-column">            <div class="pf-row">                <label class="pf-label">Full Name</label>                <input class="pf-text-field" type="text" name="name" placeholder="Type here">            </div>            <div class="pf-row">                <label class="pf-label">Company</label>                <input class="pf-text-field" type="text" name="company" placeholder="Type here">            </div>        </div>        <div class="pf-column">            <div class="pf-row">                <label class="pf-label">Telephone Number</label>                <input class="pf-text-field" type="text" name="tel" placeholder="Type here">            </div>            <div class="pf-row">                <label class="pf-label">Email</label>                <input class="pf-text-field" type="text" name="email" placeholder="Type here">            </div>        </div>    </div>    <label class="checkBox">        <input type="checkbox" name="check" value="yes">        <span class="check-title">Please contact me regarding <?= SITE_NAME ?> products and services *</span>    </label>    <button class="btn-yellow" type="submit" onclick="load('page/arrange_call', 'form:#callback_form'); return false;">Book call</button></form><script>    $("#site").addClass('popup-open');</script>