<span class="close-popup" onclick="closePopup();"><span class="icon-close"></span></span><h3 class="title-popup">Upload job vacancy</h3><form id="job_form" class="popup-form refer_job">    <div class="pf-flex">        <div class="pf-column">            <div class="pf-row">                <label class="pf-label">Your name</label>                <input class="pf-text-field" type="text" name="name" placeholder="Type your name">            </div>            <div class="pf-row">                <label class="pf-label">Email Address</label>                <input class="pf-text-field" type="text" name="email" placeholder="Type email address">            </div>        </div>        <div class="pf-column">            <div class="pf-row">                <label class="pf-label">Company Name</label>                <input class="pf-text-field" type="text" name="company" placeholder="Type company name">            </div>            <div class="pf-row">                <label class="pf-label">Telephone</label>                <input class="pf-text-field" type="text" name="tel" placeholder="Type telephone">            </div>        </div>    </div>    <div>        <div class="pf-row">            <label class="pf-label">CV file <span class="cv_file_name" style="color: #64C2C8;"></span></label>            <input class="pf-text-field" type="file" name="cv_field" style="border: none; padding: 0;"                   accept=".doc, .docx, .txt, .pdf, .fotd" onchange="initFile(this); load('about_us/upload/', 'field=#cv_field', 'preview=.cv_file_name');">            <input type="hidden" name="cv_field" id="cv_field" value="<?= post('cv_field', false); ?>">        </div>    </div>    <label class="checkBox">        <input type="checkbox" name="check" value="yes">        <span class="check-title">I have read and agree with the <a href="{URL:privacy-policy}" style="color: #64C2C8;">Privacy Policy</a></span>    </label>    <button class="btn-yellow" type="submit" onclick="load('page/upload_job', 'form:#job_form'); return false;">Send</button></form><script>    $("#site").addClass('popup-open');</script>